mapper.select:
SELECT *
FROM second_user
WHERE id = #{id, jdbcType=INTEGER}

mapper.update:
UPDATE second_user
user_name = #{userName},
`password` = #{password},
nick_name = #{nickName, jdbcType=VARCHAR}, phone = #{phone, jdbcType=CHAR}, qq = #{qq, jdbcType=CHAR}, wei_xin = #{weiXin, jdbcType=CHAR}, college = #{college, jdbcType=VARCHAR}, area = #{area, jdbcType=VARCHAR}, grade = #{grade}
WHERE id = #{id}
